html { overflow: scroll; overflow-x: hidden; }

/* CONTAINER FOR YOUR CARDS */

#kard {
    padding: 0px 0px;
    max-width: 700px; /* CHANGE CARD SIZE HERE */
    margin: 0 auto; /* CENTERS THE CARD IN THE MIDDLE OF THE WINDOW */
    word-wrap: break-word; /* BREAKS UP LONG WORKS */
}

/* STYLE TAGS TO APPEAR WHEN HOVERING OVER TOP OF CARD */

.tags {
    color: #4C566A;
    opacity: 0;
    font-size: 14px;
    width: 100%;
    text-align: center;
    position: fixed;
    padding: 0;
    top:0;
    right: 0;}
.tags:hover { opacity: 1; position: fixed;}

/* COLOR ACCENTS FOR BOLD-ITALICS-UNDERLINE */
b { color: #5E81AC; } /* BOLD STYLE */
.nightMode
b { color: #BF616A; } /* BOLD STYLE */


u { text-decoration: none; color: #2E3440;
      background-color: #E5E9F0;}  /* UNDERLINE STYLE */
.nightMode
u { text-decoration: none; color: #E5E9F0;
      background-color: #3B4252}  /* UNDERLINE STYLE */


i { color: #88C0D0; }  /* UNDERLINE STYLE */
.nightMode
i  { color: #EBCB8B; },  /* ITALICS STYLE */


a { color: #B48EAD !important; text-decoration: none; font-size: 10px; font-style: normal; } /* LINK STYLE */


.card.nightMode {

    font-family:  Arial Greek, Arial;
    font-size: 28px; /* FONT SIZE */
    text-align: center; /* ALIGN TEXT */
    color: #ECEFF4; /* FONT COLOR */
    line-height: 1.6em;
    background-color: #2E3440; /* BACKGROUND COLOR */

}

/* STYLE FOR LIGHT MODE */

.card.nightMode {
    font-family: Helvetica, Arial;
