/* Code to make header and header buttons work */
.button-tags {
  outline: 0;
  background: none !important;
  border: none !important;
  color: #93a1a1 !important;
  padding: 1px 2px !important;
  text-align: center;
  display: inline-block;
  font-size: 13px;
  margin: -3px;
  /* box-shadow: 0 8px 16px 0 rgba(0,0,0,0.2), 0 6px 20px 0 rgba(0,0,0,0.19); */
}
.button-tags:hover {
  background: none !important;
  color: #BF616A !important;
  cursor: default;
}

.button-errata {
  outline: 0;
  background: none !important;
  border: none !important;
  color: #93a1a1 !important;
  padding: 1px 2px !important;
  text-align: center;
  display: inline-block;
  font-size: 13px;
  margin: 0px;
  /* box-shadow: 0 8px 16px 0 rgba(0,0,0,0.2), 0 6px 20px 0 rgba(0,0,0,0.19); */
}
.button-errata:hover {
  background: none !important;
  color: #BF616A !important;
  cursor: default;
}

.button-nid {
  outline: 0;
  background: none !important;
  border: none !important;
  color: #93a1a1 !important;
  padding: 1px 2px !important;
  text-align: center;
  display: inline-block;
  font-size: 13px;
  margin: 0px;
  /* box-shadow: 0 8px 16px 0 rgba(0,0,0,0.2), 0 6px 20px 0 rgba(0,0,0,0.19); */
}

.button-nid:hover {
  background: none !important;
  color: #BF616A !important;
  cursor: default;
}

.header {
  padding-bottom: 0px;
  color: #93a1a1;
  font-size: 13px;
  position: relative;
  display: flex;
  top: -5px;
  word-break: break-word;
}
.headhov {
  display: inline-block;
}
.headhov:hover {
  color: #d33682;
  cursor: default;
}
.head-flex-right {
  flex: 0 0 auto;
  text-align: right;
  display: inline-block;
}
.head-flex-mid {
  flex: 1 1 auto;
  text-align: center;
  display: inline-block;
}
.ipad .header {
  padding-bottom: 4px;
}
.iphone .header {
  padding-bottom: 4px;
}
.mobile .header {
  top: 0px;
}

/* ADJUSTMENT FOR MOBILE DEVICES */
.mobile .card.nightMode { color: #ECEFF4; background-color: #2E3440; }
.mobile .card { color: #2E3440; background-color: #ECEFF4;}
.mobile .tags:hover { opacity: 1; position: relative;}

